<?phpini_set('display_errors',1);include_once('/home/gascomb/secure_html/config/set_variables.php');include_once PATH_CLASSES_FOLDER.'class.tracing.php';$id_user = $Gascomb->session_user_id();$folio_id = $_REQUEST["folio"];$Tracing = new Tracing;if(isset($_REQUEST["comments"])){		$now = time();	//$comments = explode(',',$_REQUEST["comments"]);	$comments = json_decode($_REQUEST["comments"], true);	if($comments){		foreach($comments as $comment){						if($comment !== ''){								$data['folio_id'] = $folio_id;						$data['user_id'] = $id_user;					$data['comments'] = utf8_decode($comment);									$data['creation_datetime'] = $now;					$data['modification_datetime'] = $now;					$result = $Tracing->add($data);			}		}		echo 'true';		}else{		echo 'false';	}			}?>